import React, { useState } from "react";
import { Container, Wrapper, Text, Btn } from "./NewAElements.js";
import Select from "react-select";

const options = [
  { value: "company", label: "Company" },
  { value: "smartphones", label: "Smartphones" },
  { value: "laptops", label: "Laptops" },
];
const smartphones = [{ value: "iphone12", label: "Iphone 12" }];
const companies = [{ value: "samsung", label: "Samsung" }];
const laptops = [{ value: "dellinspiron", label: "Dell Inspiron" }];

const NewA = () => {
  const [changeone, setchangeone] = useState(null);
  const [changeTwo, setchangeTwo] = useState(null);
  const [checked, setchecked] = useState(true);

  const handleChange = (e) => {
    setSelectedValue(e.value);
  };

  return (
    <>
      <Container>
        <Wrapper>
          <Text>What are you looking for ?</Text>
          <div style={{ width: "100%", "margin-bottom": "20px" }}>
            <Select
              options={options}
              onChange={(event) => changeone(event.target.value)}
            ></Select>
          </div>

          <div
            style={{
              width: "100%",
              "margin-bottom": "20px",
              display: changeone === null ? "none" : "block",
            }}
          >
            <Select
              //options={companies}
              options={changeone === "company" ? companies : laptops} //add third condition
              onChange={() => {
                setchangeTwo(true);
              }}
            ></Select>
          </div>

          <div
            style={{
              //width: "100%",
              display: "flex",
              "justify-content": "flex-end",
              color: "grey",
            }}
          >
            <label>
              <input
                style={{ margin: "0px 5px 0px 5px " }}
                type="checkbox"
                checked={checked}
                onChange={() => {
                  setchecked(!checked);
                }}
              />
              Save in history
            </label>
          </div>
          <div
            style={{
              width: "100%",
              display: "flex",
              "justify-content": "flex-end",
            }}
          >
            <Btn disabled={changeTwo}>Submit</Btn>
          </div>
        </Wrapper>
      </Container>
    </>
  );
};

export default NewA;
