import React from "react";
import { useState, useEffect } from "react";
import { Doughnut, Line, Bar } from "react-chartjs-2";

import {
  //SubWrapper,
  ExWrapper,
  //ExTxt,
  FirstRow,
  SecondRow,
  DoughnutChart,
  LineChart,
  //Title,
  FormInput,
  TitleWrapper,
  FirstCol,
  SecondCol,
  ThirdCol,
  FirstSubCol,
  SecondSubCol,
  CardSubTitleWrapper,
  CardTitle,
  CardIndc,
  SubTitle,
  DoughnutContainer,
  LineContainer,
  DataContainer,
} from "./ExElements";
//import Overview from "../Overview";
import { IoMdArrowDropupCircle } from "react-icons/io";
import { AiFillDollarCircle } from "react-icons/ai";
import { BsFillPersonFill } from "react-icons/bs";
import Switch from "react-switch";
import Skeleton from "react-loading-skeleton";

const AddData = () => {
  const [file, setfile] = useState(null);
  const [query, setquery] = useState(null);

  const fetchData = async () => {
    // eslint-disable-next-line
    const res = await fetch("http://localhost:8000/public", {
      method: "GET",
    })
      .then((res) => res.json())
      .then((res) => setfile(res));
  };

  const fetchClient = async () => {
    // eslint-disable-next-line
    const res = await fetch("http://localhost:3002/public", {
      method: "GET",
    })
      .then((res) => res.json())
      .then((res) => setfile(res));
  };

  useEffect(() => {
    fetchData();
    fetchClient();
  }, []);

  const data = {
    labels: ["EU", "NA", "JP"],
    datasets: [
      {
        label: "# of Millions",
        data: [12, 19, 3],
        backgroundColor: [
          "rgba(255, 99, 132, 0.2)",
          "rgba(54, 162, 235, 0.2)",
          "rgba(255, 206, 86, 0.2)",
        ],
        borderColor: [
          "rgba(255, 99, 132, 1)",
          "rgba(54, 162, 235, 1)",
          "rgba(255, 206, 86, 1)",
        ],
        borderWidth: 1,
      },
    ],
  };

  const data2 = {
    labels: ["1", "2", "3", "4", "5", "6"],
    datasets: [
      {
        label: "# of sales in Millions",
        data: [12, 19, 3, 5, 2, 3],
        fill: false,
        backgroundColor: "rgba(54, 162, 235, 1)",
        borderColor: "rgba(54, 162, 235, 0.2)",
      },
    ],
  };

  const options = {
    scales: {
      yAxes: [
        {
          ticks: {
            beginAtZero: true,
          },
        },
      ],
    },
  };

  const data3 = {
    labels: ["Red", "Blue", "Yellow", "Green", "Purple", "Orange"],
    datasets: [
      {
        label: "# of Votes",
        data: [12, 19, 3, 5, 2, 3],
        backgroundColor: [
          "rgba(75, 192, 192, 0.2)",
          "rgba(75, 192, 192, 0.2)",
          "rgba(75, 192, 192, 0.2)",
          "rgba(75, 192, 192, 0.2)",
          "rgba(75, 192, 192, 0.2)",
          "rgba(75, 192, 192, 0.2)",
        ],
        borderColor: [
          "rgba(75, 192, 192, 1)",
          "rgba(75, 192, 192, 1)",
          "rgba(75, 192, 192, 1)",
          "rgba(75, 192, 192, 1)",
          "rgba(75, 192, 192, 1)",
          "rgba(75, 192, 192, 1)",
        ],
        borderWidth: 1,
      },
    ],
  };

  const options2 = {
    scales: {
      yAxes: [
        {
          ticks: {
            beginAtZero: true,
          },
        },
      ],
    },
  };

  return (
    <>
      {file === null ? (
        <div
          style={{
            padding: "100px",
          }}
        >
          <div
            style={{
              "margin-bottom": "20px",
            }}
          >
            <Skeleton height={200} />
          </div>
          <div
            style={{
              "margin-bottom": "20px",
            }}
          >
            <Skeleton height={40} />
          </div>
          <div
            style={{
              "margin-bottom": "20px",
            }}
          >
            <Skeleton height={40} />
          </div>
          <div
            style={{
              "margin-bottom": "20px",
            }}
          >
            <Skeleton height={40} />
          </div>
          <div
            style={{
              "margin-bottom": "20px",
            }}
          >
            <Skeleton height={130} />
          </div>
          <div>
            <Skeleton height={40} />
          </div>
        </div>
      ) : (
        <>
          <DataContainer>
            <DoughnutContainer>
              <SubTitle style={{ "margin-bottom": "20px" }}>
                Positif, Negatif and Neutural Distribution
              </SubTitle>
              <Doughnut
                data={{
                  labels: ["Negatif", "Neutral", "Positif"],
                  datasets: [
                    {
                      label: "# of Tweets",
                      data: [
                        query.query.negative_tweets,
                        query.query.neutral_tweets,
                        query.query.positive_tweets,
                      ],
                      backgroundColor: [
                        "rgba(255, 99, 132, 0.2)",
                        "rgba(54, 162, 235, 0.2)",
                        "#CDF3A2",
                      ],
                      borderColor: [
                        "rgba(255, 99, 132, 1)",
                        "rgba(54, 162, 235, 1)",
                        "#57837B",
                      ],
                      borderWidth: 1,
                    },
                  ],
                }}
              ></Doughnut>
            </DoughnutContainer>
            <LineContainer>
              <div
                style={{
                  width: "100%",
                  display: "flex",
                  "flex-direction": "row",
                  "justify-content": "space-between",
                }}
              >
                <SubTitle style={{ "margin-bottom": "20px" }}>
                  {checkedf ? "Weekly" : "Daily"} Line of Tweets Sentiment
                </SubTitle>
                <Switch
                  checked={checkedf}
                  onChange={handleChangef}
                  onColor="#86d3ff"
                  onHandleColor="#096192"
                  handleDiameter={30}
                  uncheckedIcon={false}
                  checkedIcon={false}
                  boxShadow="0px 1px 5px rgba(0, 0, 0, 0.6)"
                  activeBoxShadow="0px 0px 1px 10px rgba(0, 0, 0, 0.2)"
                  height={20}
                  width={48}
                />
              </div>
              <Line
                data={{
                  labels: checkedf ? query.weekdates : query.dates,
                  datasets: [
                    {
                      label: "# of positif tweets",
                      data: checkedf ? query.weekpos : query.ratpos,
                      fill: false,
                      backgroundColor: "rgba(54, 162, 235, 1)",
                      borderColor: "rgba(54, 162, 235, 0.2)",
                    },
                    {
                      label: "# of negatif tweets",
                      data: checkedf ? query.weekneg : query.ratneg,
                      fill: false,
                      backgroundColor: "rgba(255, 99, 132, 1)",
                      borderColor: "rgba(255, 99, 132, 0.2)",
                    },
                  ],
                }}
                options={{
                  scales: {
                    yAxes: [
                      {
                        ticks: {
                          beginAtZero: true,
                        },
                      },
                    ],
                  },
                }}
              />
            </LineContainer>
          </DataContainer>
        </>
      )}
    </>
  );
};

export default AddData;
