import React from "react";
import { useState, useEffect } from "react";

import {
  SubWrapper,
  ExWrapper,
  AddImage,
  AddBtn,
  AddBtnT,
  AddTxt,
  AddTxtT,
  TableWrapper,
  OptWrapper,
  Del,
  SelectedWrapper,
  TxtWrapper,
} from "./ExElements";
import { AiFillFolderAdd, AiFillDelete } from "react-icons/ai";
import { FiUpload } from "react-icons/fi";
import { ToastContainer, toast, Slide } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";

const AddData = () => {
  const [file, setfile] = useState(null);
  const [selected, setselected] = useState(null);
  const [show, setshow] = useState(false);

  const fetchData = async () => {
    // eslint-disable-next-line
    const res = await fetch("http://localhost:8000/public", {
      method: "GET",
    })
      .then((res) => res.json())
      .then((res) => setfile(res));
  };

  useEffect(() => {
    fetchData();
  }, []);

  const theme = createMuiTheme({
    typography: {
      fontFamily: ["Open Sans"].join(","),
    },
  });

  const hiddenFileInput = React.useRef(null);

  const handleClick = (event) => {
    hiddenFileInput.current.click();
  };

  const handleAction = async (data) => {
    const formData = new FormData();
    formData.append("file", selected);
    const res = await fetch("http://localhost:8000/public", {
      method: "POST",
      body: formData,
    }).then((res) => res.json());
    if (res.status === true) {
      toast.success("Upload succesful!", {
        position: "bottom-center",
        autoClose: 2000,
        hideProgressBar: false,
        closeOnClick: true,
        pauseOnHover: false,
        draggable: true,
        progress: undefined,
      });
    } else {
      toast.error("Upload failed!", {
        position: "bottom-center",
        autoClose: 2000,
        hideProgressBar: false,
        closeOnClick: true,
        pauseOnHover: false,
        draggable: true,
        progress: undefined,
      });
    }
    setfile(false);
    setshow(false);
    fetchData();
  };

  const handleChange = (event) => {
    setselected(event.target.files[0]);
    setshow(true);
  };

  const clickDel = async () => {
    const res = await fetch("http://localhost:8000/public", {
      method: "DELETE",
    }).then((res) => res.json());
    if (res.status === true) {
      toast.success("Delete succesful!", {
        position: "bottom-center",
        autoClose: 2000,
        hideProgressBar: false,
        closeOnClick: true,
        pauseOnHover: false,
        draggable: true,
        progress: undefined,
      });
    } else {
      toast.error("Delete failed!", {
        position: "bottom-center",
        autoClose: 2000,
        hideProgressBar: false,
        closeOnClick: true,
        pauseOnHover: false,
        draggable: true,
        progress: undefined,
      });
    }
    setfile(null);
    //fetchData();
  };

  return (
    <>
      {file === null ? (
        <ExWrapper>
          <SubWrapper>
            <AddTxt> Click on DATASET to upload a new dataset first!</AddTxt>
          </SubWrapper>
        </ExWrapper>
      ) : (
        <ExWrapper></ExWrapper>
      )}
    </>
  );
};

export default AddData;
