import React, { useState, useEffect } from "react";
import SideBarDD from "../SideBarD";
import ExploreData from "./ExploreData";
import WelcomeBarD from "../WelcomeBar";
import { Redirect } from "react-router-dom";
import {
  SideBarContainer,
  ContentContainer,
  DashboardContainer,
} from "./ExploreElements";
import { AiFillHome } from "react-icons/ai";

const DashExplore = () => {
  const tellPath = () => {
    return (
      <>
        <AiFillHome style={{ "margin-right": "5px" }}></AiFillHome>
        /Explore
      </>
    );
  };

  const [redirect, setRedirect] = useState(true);
  const checkAuth = () => {
    const item = localStorage.getItem("token");
    if (item) {
      console.log("connect first");
      setRedirect(false);
    } else {
      console.log("connect first");
    }
  };
  useEffect(() => {
    checkAuth();
  }, []);

  if (redirect) {
    return <Redirect to="/signin" />;
  }else

  return (
    <>
      <DashboardContainer>
        <SideBarContainer>
          <SideBarDD></SideBarDD>
        </SideBarContainer>
        <ContentContainer>
          <WelcomeBarD path={tellPath()}></WelcomeBarD>
          <ExploreData></ExploreData>
        </ContentContainer>
      </DashboardContainer>
    </>
  );
};

export default DashExplore;
