import React from "react";
import { useState, useEffect } from "react";
import { Doughnut, Line } from "react-chartjs-2";

import {
  SubWrapper,
  ExWrapper,
  ExTxt,
  FirstRow,
  SecondRow,
  ThirdRow,
  DoughnutChart,
  LineChart,
} from "./ExElements";

const AddData = () => {
  const [file, setfile] = useState(null);

  const fetchData = async () => {
    // eslint-disable-next-line
    const res = await fetch("http://localhost:8000/public", {
      method: "GET",
    })
      .then((res) => res.json())
      .then((res) => setfile(res));
  };

  useEffect(() => {
    fetchData();
  }, []);

  /*
  const hiddenFileInput = React.useRef(null);

  const handleClick = (event) => {
    hiddenFileInput.current.click();
  };


  const handleAction = async (data) => {
    const formData = new FormData();
    formData.append("file", selected);
    const res = await fetch("http://localhost:8000/public", {
      method: "POST",
      body: formData,
    }).then((res) => res.json());
    if (res.status === true) {
      toast.success("Upload succesful!", {
        position: "bottom-center",
        autoClose: 2000,
        hideProgressBar: false,
        closeOnClick: true,
        pauseOnHover: false,
        draggable: true,
        progress: undefined,
      });
    } else {
      toast.error("Upload failed!", {
        position: "bottom-center",
        autoClose: 2000,
        hideProgressBar: false,
        closeOnClick: true,
        pauseOnHover: false,
        draggable: true,
        progress: undefined,
      });
    }
    setfile(false);
    setshow(false);
    fetchData();
  };
  */

  const data = {
    labels: ["EU", "NA", "JP"],
    datasets: [
      {
        label: "# of Millions",
        data: [12, 19, 3],
        backgroundColor: [
          "rgba(255, 99, 132, 0.2)",
          "rgba(54, 162, 235, 0.2)",
          "rgba(255, 206, 86, 0.2)",
        ],
        borderColor: [
          "rgba(255, 99, 132, 1)",
          "rgba(54, 162, 235, 1)",
          "rgba(255, 206, 86, 1)",
        ],
        borderWidth: 1,
      },
    ],
  };

  const data2 = {
    labels: ["1", "2", "3", "4", "5", "6"],
    datasets: [
      {
        label: "# of Votes",
        data: [12, 19, 3, 5, 2, 3],
        fill: false,
        backgroundColor: "rgb(255, 99, 132)",
        borderColor: "rgba(255, 99, 132, 0.2)",
      },
    ],
  };

  const options = {
    scales: {
      yAxes: [
        {
          ticks: {
            beginAtZero: true,
          },
        },
      ],
    },
  };

  return (
    <>
      {file === null ? (
        <ExWrapper>
          <SubWrapper>
            <ExTxt> Click on DATASET to upload a new dataset first!</ExTxt>
          </SubWrapper>
        </ExWrapper>
      ) : (
        <ExWrapper>
          <FirstRow>
            <LineChart>
              hello
              <Line data={data2} options={options} />
            </LineChart>
            <DoughnutChart>
              <Doughnut data={data2}></Doughnut>
            </DoughnutChart>
          </FirstRow>
        </ExWrapper>
      )}
    </>
  );
};

export default AddData;
