const CircleSearch = styled.input`
  width: ${(p) => (p.isOpen ? "100%" : "8rem")};
  transition: width 0.75s cubic-bezier(0, 0.795, 0, 1);
`;

const WelcomeBarD = ({ path, isOpen, onSearchFocus, onSearchBlur }) => {
  return (
    <>
      <WelcomeBar>
        <WelcomeBarWrapper>
          <TextWrapper>
            <WelcomeText>WELCOME BACK ACME INC. ðŸ‘‹</WelcomeText>
            <WelcomeSubText>{path}</WelcomeSubText>
