            <CircleSearch
              placeholder="Search"
              isOpen={isOpen}
              onFocus={onSearchFocus}
              onBlur={onSearchBlur}
            ></CircleSearch>

            <Badge badgeContent={0} color="primary" showZero>
              <Circle>
                <BiMessageSquareDetail size="20px"></BiMessageSquareDetail>
