import styled from "styled-components";
import { Link as LinkR } from "react-router-dom";
//import { Link as LinkS } from "react-scroll";

export const AccWrapper = styled.div`
  display: flex;
  flex-direction: column;
  //justify-content: center;
  align-items: center;
  width: 100%;
  height: 900px;
  background: #fff;
  padding: 50px;
`;

export const AccContainer = styled.div`
  display: flex;
  flex-direction: row;
  width: 100%;
  height: 80%;
  //background: #f3f4ed;
  box-shadow: 0px 0px 30px 1px #f3f4ed;
`;

export const AccBar = styled.div`
  display: flex;
  background: #f3f4ed;
  flex: 1;
  flex-direction: column;
  align-items: flex-start;
  justify-content: space-evenly;
`;

export const AccPh = styled.div``;
export const AccTxt = styled.div``;
export const AccLo = styled(LinkR)``;

export const AccInfoContainer = styled.div`
  background: #fff;
  display: flex;
  flex: 4;
`;
