import styled from "styled-components";
import { Link as LinkR } from "react-router-dom";
//import { Link as LinkS } from "react-scroll";

export const AccWrapper = styled.div`
  display: flex;
  flex-direction: column;
  //justify-content: center;
  align-items: center;
  width: 100%;
  height: 900px;
  background: #fff;
  padding: 50px;
`;

export const AccContainer = styled.div`
  display: flex;
  flex-direction: row;
  width: 100%;
  height: 80%;
  //background: #f3f4ed;
  box-shadow: 0px 0px 30px 1px #f3f4ed;
`;

export const AccBar = styled.div`
  display: flex;
  background: #f3f4ed;
  flex: 1;
  flex-direction: column;
  align-items: center;
  justify-content: space-between;
  padding: 20px; ;
`;

export const AccPhContainer = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
`;

export const AccPh = styled.img`
  width: 100px;
  height: 100px;
  margin-bottom: 10px;
`;

export const AccTxt = styled.p`
  text-align: center;
  font-family: "Open Sans", sans-serif;
`;

export const AccLo = styled(LinkR)`
  background: #096192;
  border-radius: 50px;
  height: 50px;
  width: 100%;
  color: #fff;
  text-decoration: none;
  &:hover {
    transition: all 0.2s ease-in-out;
    background: #053742;
    color: #fff;
  }
`;

export const AccInfoContainer = styled.div`
  background: #fff;
  display: flex;
  flex: 4;
`;
