import React, { useState, useEffect } from "react";
import {
  Title,
  LeftContainer,
  MainText,
  SubTitle,
  OverviewContainer,
  RightContainer,
  AddBtn,
  DoughnutContainer,
  DataContainer,
  LineContainer,
  BigTitle,
  Wrapper,
  Head,
  Filler,
  InsightContainer,
} from "./MainElements";
import { BsFillCaretDownFill, BsFillCaretUpFill } from "react-icons/bs";
import img from "../../../../images/svg-12.svg";
import Skeleton from "react-loading-skeleton";
import { Doughnut, Line, Bar, PolarArea } from "react-chartjs-2";
import Switch from "react-switch";

const Main = () => {
  const [query, setquery] = useState(null);
  const [social, setsocial] = useState(true);
  const [comp, setcomp] = useState(true);
  const [checked, setchecked] = useState(false);
  const [checkedf, setcheckedf] = useState(false);
  const [checkedff, setcheckedff] = useState(false);

  const fetchData = async () => {
    // eslint-disable-next-line
    const res = await fetch("http://localhost:3002/getquery", {
      method: "GET",
    })
      .then((res) => res.json())
      .then((res) => setquery(res));
  };
  useEffect(() => {
    fetchData();
  }, []);

  const handleSocial = () => {
    setsocial(!social);
  };

  const handleComparatif = () => {
    setcomp(!comp);
  };

  const handleChange = () => {
    setchecked(!checked);
  };

  const handleChangef = () => {
    setcheckedf(!checkedf);
  };

  const handleChangeff = () => {
    setcheckedff(!checkedff);
  };

  const getDay = () => {
    var i = query.days.indexOf(Math.max(...query.days));
    var day;
    switch (i) {
      case 0:
        day = "Monday";
        break;
      case 1:
        day = "Thuesday";
        break;
      case 2:
        day = "Wednesday";
        break;
      case 3:
        day = "Thursday";
        break;
      case 4:
        day = "Friday";
        break;
      case 5:
        day = "Saturday";
        break;
      case 6:
        day = "Sunday";
        break;
      default:
        break;
    }
    return day;
  };

  return (
    <>
      {query === null ? (
        <div
          style={{
            padding: "100px",
          }}
        >
          <div
            style={{
              "margin-bottom": "20px",
            }}
          >
            <Skeleton height={200} />
          </div>
          <div
            style={{
              "margin-bottom": "20px",
            }}
          >
            <Skeleton height={40} />
          </div>
          <div
            style={{
              "margin-bottom": "20px",
            }}
          >
            <Skeleton height={40} />
          </div>
          <div
            style={{
              "margin-bottom": "20px",
            }}
          >
            <Skeleton height={40} />
          </div>
          <div
            style={{
              "margin-bottom": "20px",
            }}
          >
            <Skeleton height={130} />
          </div>
          <div>
            <Skeleton height={40} />
          </div>
        </div>
      ) : (
        <>
          <OverviewContainer>
            <RightContainer>
              <Title>{query.query.query}</Title>
              <SubTitle>Explore these tweets with our tool.</SubTitle>
