import React, { useState, useEffect } from "react";
import {Title} from "./MainElements";

import Skeleton from "react-loading-skeleton";

const Main = () => {
  const [query, setquery] = useState(null);
  const fetchData = async () => {
    // eslint-disable-next-line
    const res = await fetch("http://localhost:3002/getquery", {
      method: "GET",
    })
      .then((res) => res.json())
      .then((res) => setquery(res));
  };
  useEffect(() => {
    fetchData();
  }, []);

  return (
    <>
      {query === null ? (
        <div
          style={{
            padding: "100px",
          }}
        >
          <div
            style={{
              "margin-bottom": "20px",
            }}
          >
            <Skeleton height={200} />
          </div>
          <div
            style={{
              "margin-bottom": "20px",
            }}
          >
            <Skeleton height={40} />
          </div>
          <div
            style={{
              "margin-bottom": "20px",
            }}
          >
            <Skeleton height={40} />
          </div>
          <div
            style={{
              "margin-bottom": "20px",
            }}
          >
            <Skeleton height={40} />
          </div>
        </div>
      ) : (
        <>
          <OverviewContainer>
            <RightContainer>
              <Title>SOCIAL OVERVIEW</Title>
              <SubTitle>Start a new analysis now to explore:</SubTitle>
              <MainText>More than 56'112 analysed tweets.</MainText>
              <SubText>Over 20 brands and companies.</SubText>
              <SubText>More than 60 products covered.</SubText>
              <AddBtn to="/newanalysis">Add new analysis</AddBtn>
            </RightContainer>
            <LeftContainer src={img}></LeftContainer>
          </OverviewContainer>
        </>
      )}
    </>
  );
};

export default Main;
