import React, { useState, useEffect } from "react";
import {
  Title,
  LeftContainer,
  MainText,
  SubTitle,
  OverviewContainer,
  RightContainer,
  AddBtn,
  DoughnutContainer,
  DataContainer,
  LineContainer,
  BigTitle,
} from "./MainElements";
import { BsFillCaretDownFill, BsFillCaretUpFill } from "react-icons/bs";
import img from "../../../../images/svg-12.svg";
import Skeleton from "react-loading-skeleton";
import { Doughnut, Line, Bar, PolarArea } from "react-chartjs-2";
import Switch from "react-switch";

const Main = () => {
  const [query, setquery] = useState(null);
  const [social, setsocial] = useState(true);
  const [comp, setcomp] = useState(true);
  const fetchData = async () => {
    // eslint-disable-next-line
    const res = await fetch("http://localhost:3002/getquery", {
      method: "GET",
    })
      .then((res) => res.json())
      .then((res) => setquery(res));
  };
  useEffect(() => {
    fetchData();
  }, []);

  const handleSocial = () => {
    setsocial(!social);
  };

  const handleComparatif = () => {
    setcomp(!comp);
  };

  return (
    <>
      {query === null ? (
        <div
          style={{
            padding: "100px",
          }}
        >
          <div
            style={{
              "margin-bottom": "20px",
            }}
          >
            <Skeleton height={200} />
          </div>
          <div
            style={{
              "margin-bottom": "20px",
            }}
          >
            <Skeleton height={40} />
          </div>
          <div
            style={{
              "margin-bottom": "20px",
            }}
          >
            <Skeleton height={40} />
          </div>
          <div
            style={{
              "margin-bottom": "20px",
            }}
          >
            <Skeleton height={40} />
          </div>
        </div>
      ) : (
        <>
          <OverviewContainer>
            <RightContainer>
              <Title>{query.query.query}</Title>
              <SubTitle>Explore these tweets with our tool.</SubTitle>
              <MainText>
                More than {query.query.total_tweets} analysed tweets.
              </MainText>
              <AddBtn to="/newanalysis">Add new analysis</AddBtn>
            </RightContainer>
            <LeftContainer src={img}></LeftContainer>
          </OverviewContainer>
          <BigTitle onClick={handleSocial}>
            <div>
              <div>{"SOCIAL ANALYSIS"}</div>
              <div style={{ "font-size": "18px" }}>
                From {query.dates[0]} TO {query.dates.at(-1)}
              </div>
            </div>
            {social === true ? (
              <BsFillCaretUpFill></BsFillCaretUpFill>
            ) : (
              <BsFillCaretDownFill></BsFillCaretDownFill>
            )}
          </BigTitle>
          <DataContainer display={social}>
            <DoughnutContainer>
              <SubTitle style={{ "margin-bottom": "20px" }}>
                Positif, Negatif and Neutural Distribution
              </SubTitle>
              <Doughnut
                data={{
                  labels: ["Negatif", "Neutral", "Positif"],
                  datasets: [
                    {
                      label: "# of Tweets",
                      data: [
                        query.query.negative_tweets,
                        query.query.neutral_tweets,
                        query.query.positive_tweets,
                      ],
                      backgroundColor: [
                        "rgba(255, 99, 132, 0.2)",
                        "rgba(54, 162, 235, 0.2)",
                        "#CDF3A2",
                      ],
                      borderColor: [
                        "rgba(255, 99, 132, 1)",
                        "rgba(54, 162, 235, 1)",
                        "#57837B",
                      ],
                      borderWidth: 1,
                    },
                  ],
                }}
              ></Doughnut>
            </DoughnutContainer>
            <LineContainer>
              <SubTitle style={{ "margin-bottom": "20px" }}>
                Daily Line of Tweets Sentiment
              </SubTitle>
              <Line
                data={{
                  labels: query.dates,
                  datasets: [
                    {
                      label: "# of positif tweets",
                      data: query.ratpos,
                      fill: false,
                      backgroundColor: "rgba(54, 162, 235, 1)",
                      borderColor: "rgba(54, 162, 235, 0.2)",
                    },
                    {
                      label: "# of negatif tweets",
                      data: query.ratneg,
                      fill: false,
                      backgroundColor: "rgba(255, 99, 132, 1)",
                      borderColor: "rgba(255, 99, 132, 0.2)",
                    },
                  ],
                }}
                options={{
                  scales: {
                    yAxes: [
                      {
                        ticks: {
                          beginAtZero: true,
                        },
                      },
                    ],
                  },
                }}
              />
            </LineContainer>
          </DataContainer>
          <DataContainer display={social}>
            <DoughnutContainer>
              <SubTitle style={{ "margin-bottom": "20px" }}>
                Tweets per Days of the Week
              </SubTitle>
              <PolarArea
                data={{
                  labels: [
                    "Monday",
                    "Tuesday",
                    "Wednesday",
                    "Thursday",
                    "Friday",
                    "Saturday",
                    "Sunday",
                  ],
                  borderWidth: 1,
                  datasets: [
                    {
                      label: "# of Tweets",
                      data: [
                        query.days[0],
                        query.days[1],
                        query.days[2],
                        query.days[3],
                        query.days[4],
                        query.days[5],
                        query.days[6],
                      ],
                      backgroundColor: [
                        "rgba(255, 99, 132, 0.5)",
                        "rgba(54, 162, 235, 0.5)",
                        "rgba(255, 206, 86, 0.5)",
                        "rgba(75, 192, 192, 0.5)",
                        "rgba(153, 102, 255, 0.5)",
                        "rgba(255, 159, 64, 0.5)",
                        "#CDF352",
                      ],
                    },
                  ],
                }}
              ></PolarArea>
            </DoughnutContainer>
            <LineContainer>
              <div
                style={{
                  width: "100%",
                  display: "flex",
                  "flex-direction": "row",
                  "justify-content": "space-between",
                }}
              >
                <SubTitle style={{ "margin-bottom": "20px" }}>
                  Weekly Trending Evolution
                </SubTitle>
                <Switch
                  //checked={this.state.checked}
                  //onChange={this.handleChange}
                  onColor="#86d3ff"
                  onHandleColor="#2693e6"
                  handleDiameter={30}
                  uncheckedIcon={false}
                  checkedIcon={false}
                  boxShadow="0px 1px 5px rgba(0, 0, 0, 0.6)"
                  activeBoxShadow="0px 0px 1px 10px rgba(0, 0, 0, 0.2)"
                  height={20}
                  width={48}
                />
              </div>

              <Bar
                data={{
                  labels: query.weekdates,
                  datasets: [
                    {
                      label: "# of tweets",
                      data: query.weekly,
                      fill: false,
                      backgroundColor: "rgba(54, 162, 235, 1)",
                      borderColor: "rgba(54, 162, 235, 0.2)",
                    },
                  ],
                }}
                options={{
                  scales: {
                    yAxes: [
                      {
                        ticks: {
                          beginAtZero: true,
                        },
                      },
                    ],
                  },
                }}
              />
            </LineContainer>
          </DataContainer>
          <BigTitle onClick={handleComparatif}>
            <div>
              <div>{"COMPARATIF ANALYSIS"}</div>
              <div style={{ "font-size": "18px" }}>
                From {query.dates[0]} TO {query.dates.at(-1)}
              </div>
            </div>
            {comp === true ? (
              <BsFillCaretUpFill></BsFillCaretUpFill>
            ) : (
              <BsFillCaretDownFill></BsFillCaretDownFill>
            )}
          </BigTitle>
        </>
      )}
    </>
  );
};

export default Main;
