import styled from "styled-components";

export const HeroContainer = styled.div`
  background: #0c0c0c;
  height: 800px;
  display: flex;
  justify-content: center;
  align-items: center;
  posiiton: relative;
  z-index: 1;
`;

export const HeroBg = styled.div`
  height: 100%;
  posiiton: absolute;
  top: 0;
  right: 0;
  bottom: 0;
  left: 0;
  width: 100%;
  overflow: hidden;
`;

export const VideoBg = styled.video`
  width: 100%;
  height: 100%;
  -o-object-fit: cover;
  object-fit: cover;
  background: #232a34;
`;
